import React from 'react';
import { Link } from 'react-router-dom';
// import './Card.css'; // Assuming your styles are in a separate CSS file
// import rightArrowIcon from '../Assets/rightArrowIcon.png';

const Card = ({ title, itemCount, linkTo }) => (
  <div className={`col-4`}>
    <div className={`card text-dark rounded-0 crd card-${title.toLowerCase().replace(' ', '-')}`}>
      <div className="card-body p-5">
        <h4 className="card-title">{title}</h4>
        <h6 className="card-title">{itemCount} Items</h6>
        <Link to={linkTo} className="nav-link p-1" style={{ flex: '1' }}>
          <button className="btn btn-transparent btn-outline-dark mt-5 rounded-0 hbtn" style={{ display: 'flex', alignItems: 'center' }}>
            Browse Items
            <img src={rightArrowIcon} alt="arrowLogo" className="p-1" />
          </button>
        </Link>
      </div>
    </div>
  </div>
);

const Cards = () => (
  <div className="container-fluid mt-5">
    <div className="row">
      {/* <Card title="Period Care" itemCount={8685} linkTo="shop" /> */}
      {/* <Card title="Skin Care" itemCount={8685} linkTo="shop" /> */}
      {/* <Card title="Health Care" itemCount={32} linkTo="shop" /> */}
    </div>
  </div>
);

export default Cards;
