                    data.map((bitem) => {
                        if (item.id == bitem.id) {
                            if(item.offer <= 0){
                              return  (bitem.quantity * item.price) ;
                            }else{
                              return Math.abs(bitem.quantity * (Math.floor((item.offer / 100) * item.price)-item.price));

                            }
                        }
                    }

                    )
                }
                </b>
                {/* {item.price} */}
                {/* {productPrice} */}

            </p>
            <p></p>
        </div>
    )
}
